    private void plusBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_plusBtnActionPerformed
        
        int num1 = Integer.parseInt(txtNum1.getText());
        int num2 = Integer.parseInt(txtNum2.getText());
        
        int ans = num1 + num2;
        
        result.setText(String.valueOf(ans));
        
    }//GEN-LAST:event_plusBtnActionPerformed
	
	
	private void subtrBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_subtrBtnActionPerformed
        
        int num1 = Integer.parseInt(txtNum1.getText());
        int num2 = Integer.parseInt(txtNum2.getText());
        
        int ans = num1 - num2;
        
        result.setText(String.valueOf(ans));
    }//GEN-LAST:event_subtrBtnActionPerformed
	
	
	private void multiplyBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_multiplyBtnActionPerformed
        int num1 = Integer.parseInt(txtNum1.getText());
        int num2 = Integer.parseInt(txtNum2.getText());
        
        int ans = num1 * num2;
        
        result.setText(String.valueOf(ans));
    }//GEN-LAST:event_multiplyBtnActionPerformed
	
	
	private void divdBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_divdBtnActionPerformed
        float num1 = Float.parseFloat(txtNum1.getText());
        float num2 = Float.parseFloat(txtNum2.getText());
        
        float ans = num1 / num2;
        
        result.setText(String.valueOf(ans));
    }//GEN-LAST:event_divdBtnActionPerformed


